mapper.select:
select
from groups where parent_id is null
and group_name = #{parameter.groupName}
order by create_time desc LIMIT #{pageSize,jdbcType=DECIMAL} offset #{offset,jdbcType=DECIMAL}

select
from groups where parent_id is not null
and group_name = #{parameter.groupName}
order by create_time desc LIMIT #{pageSize,jdbcType=DECIMAL} offset #{offset,jdbcType=DECIMAL}
